import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Shield, Lock, Eye, EyeOff } from 'lucide-react';

export default function AdminLogin() {
  const [, setLocation] = useLocation();
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });

    // Vérifier si déjà connecté
    const token = localStorage.getItem('adminToken');
    if (token) {
      checkAuthStatus(token);
    }
  }, []);

  const checkAuthStatus = async (token: string) => {
    try {
      const response = await fetch('/api/admin/status', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setLocation('/admin/dashboard');
      } else {
        localStorage.removeItem('adminToken');
      }
    } catch (error) {
      localStorage.removeItem('adminToken');
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/api/admin/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem('adminToken', data.token);
        setLocation('/admin/dashboard');
      } else {
        setError(data.message || 'Identifiants incorrects');
      }
    } catch (error) {
      setError('Erreur de connexion. Veuillez réessayer.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
    setError('');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-background to-muted p-4">
      <div className="w-full max-w-md">

        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <Shield className="w-12 h-12 text-primary" />
          </div>
          <h1 className="text-3xl font-bold mb-2">Administration Sécurisée</h1>
          <p className="text-muted-foreground">
            Accès protégé - Weblify Studio
          </p>
          <Badge variant="destructive" className="mt-2">
            Zone Sécurisée
          </Badge>
        </div>

        {/* Formulaire de connexion */}
        <Card className="border-2">
          <CardHeader>
            <CardTitle className="text-center flex items-center justify-center gap-2">
              <Lock className="w-5 h-5" />
              Connexion Administrateur
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">

              {/* Username */}
              <div className="space-y-2">
                <Label htmlFor="username">Nom d'utilisateur</Label>
                <Input
                  id="username"
                  name="username"
                  type="text"
                  value={formData.username}
                  onChange={handleChange}
                  placeholder="Entrez votre nom d'utilisateur"
                  required
                  autoComplete="username"
                  className="border-2 focus:border-primary"
                />
              </div>

              {/* Password */}
              <div className="space-y-2">
                <Label htmlFor="password">Mot de passe</Label>
                <div className="relative">
                  <Input
                    id="password"
                    name="password"
                    type={showPassword ? 'text' : 'password'}
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Entrez votre mot de passe"
                    required
                    autoComplete="current-password"
                    className="border-2 focus:border-primary pr-10"
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? (
                      <EyeOff className="w-4 h-4" />
                    ) : (
                      <Eye className="w-4 h-4" />
                    )}
                  </Button>
                </div>
              </div>

              {/* Error */}
              {error && (
                <div className="bg-destructive/10 border border-destructive/20 rounded-lg p-3">
                  <p className="text-sm text-destructive font-medium">{error}</p>
                </div>
              )}

              {/* Submit */}
              <Button 
                type="submit" 
                className="w-full" 
                disabled={loading}
              >
                {loading ? 'Connexion...' : 'Se connecter'}
              </Button>
            </form>

            {/* Info sécurité */}
            <div className="mt-6 p-4 bg-muted rounded-lg">
              <div className="flex items-start gap-3">
                <Shield className="w-5 h-5 text-primary mt-0.5" />
                <div className="text-sm">
                  <p className="font-medium mb-1">Sécurité renforcée</p>
                  <ul className="text-muted-foreground space-y-1">
                    <li>• Sessions chiffrées</li>
                    <li>• Expiration automatique (24h)</li>
                    <li>• Protection anti-brute force</li>
                    <li>• Aucune trace persistante</li>
                  </ul>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Development info */}
        {process.env.NODE_ENV === 'development' && (
          <div className="mt-4 p-3 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg">
            <p className="text-sm text-yellow-800 dark:text-yellow-200">
              <strong>Mode développement :</strong> Utilisez admin/admin123 si aucun mot de passe n'est configuré
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Mail, MailOpen, Clock, User, Phone, Euro, MessageSquare, LogOut, Shield } from 'lucide-react';

interface Email {
  id: string;
  fromName: string;
  fromEmail: string;
  toEmail: string;
  subject: string;
  content: string;
  isRead: boolean;
  createdAt: string;
}

export default function Admin() {
  const [, setLocation] = useLocation();
  const [emails, setEmails] = useState<Email[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedEmail, setSelectedEmail] = useState<Email | null>(null);
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    checkAuthentication();
  }, []);

  const checkAuthentication = async () => {
    const token = localStorage.getItem('adminToken');
    if (!token) {
      setLocation('/admin/login');
      return;
    }

    try {
      const response = await fetch('/api/admin/status', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setAuthenticated(true);
        fetchEmails();
      } else {
        localStorage.removeItem('adminToken');
        setLocation('/admin/login');
      }
    } catch (error) {
      localStorage.removeItem('adminToken');
      setLocation('/admin/login');
    }
  };

  const handleLogout = async () => {
    const token = localStorage.getItem('adminToken');
    if (token) {
      try {
        await fetch('/api/admin/logout', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
      } catch (error) {
        console.error('Logout error:', error);
      }
    }

    localStorage.removeItem('adminToken');
    setLocation('/admin/login');
  };

  const fetchEmails = async () => {
    const token = localStorage.getItem('adminToken');
    if (!token) return;

    try {
      const response = await fetch('/api/admin/emails', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setEmails(data.emails || []);
      }
    } catch (error) {
      console.error('Erreur lors du chargement des emails:', error);
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (emailId: string) => {
    const token = localStorage.getItem('adminToken');
    if (!token) return;

    try {
      const response = await fetch(`/api/admin/emails/${emailId}/read`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setEmails(emails.map(email => 
          email.id === emailId ? { ...email, isRead: true } : email
        ));
      }
    } catch (error) {
      console.error('Erreur lors du marquage comme lu:', error);
    }
  };

  const parseEmailContent = (content: string) => {
    const lines = content.split('\n');
    const data: any = {};

    for (const line of lines) {
      if (line.includes('- Nom :')) data.name = line.split('- Nom :')[1]?.trim();
      if (line.includes('- Email :')) data.email = line.split('- Email :')[1]?.trim();
      if (line.includes('- Téléphone :')) data.phone = line.split('- Téléphone :')[1]?.trim();
      if (line.includes('- Budget :')) data.budget = line.split('- Budget :')[1]?.trim();
      if (line.includes('- Type de projet :')) data.projectType = line.split('- Type de projet :')[1]?.trim();
      if (line.includes('- Newsletter :')) data.newsletter = line.split('- Newsletter :')[1]?.trim();
    }

    const messageStart = content.indexOf('Message :');
    if (messageStart !== -1) {
      const messageEnd = content.indexOf('Date :');
      data.message = content.substring(messageStart + 9, messageEnd !== -1 ? messageEnd : content.length).trim();
    }

    return data;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString('fr-FR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!authenticated || loading) {
    return (
      <div className="pt-28 pb-16">
        <div className="container mx-auto px-4">
          <div className="text-center">
            <h1 className="text-3xl font-bold mb-8">
              {!authenticated ? 'Vérification de l\'authentification...' : 'Chargement des emails...'}
            </h1>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="pt-28 pb-16">
      <div className="container mx-auto px-4">
        <div className="max-w-7xl mx-auto">

          {/* Header */}
          <div className="flex items-center justify-between mb-8">
            <div>
              <h1 className="text-4xl font-bold mb-2 flex items-center gap-3">
                <Shield className="w-8 h-8 text-primary" />
                Administration Sécurisée
              </h1>
              <p className="text-xl text-muted-foreground">
                Consultation des messages de contact - Session protégée
              </p>
            </div>
            <div className="flex gap-3">
              <Button 
                onClick={() => setLocation('/admin/compose')}
                className="flex items-center gap-2"
              >
                <Mail className="w-4 h-4" />
                Composer
              </Button>
              <Button 
                onClick={handleLogout}
                variant="outline"
                className="flex items-center gap-2"
              >
                <LogOut className="w-4 h-4" />
                Déconnexion
              </Button>
            </div>
          </div>

          {/* Stats */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <Card>
              <CardContent className="p-6 text-center">
                <Mail className="w-8 h-8 mx-auto mb-2 text-primary" />
                <div className="text-2xl font-bold">{emails.length}</div>
                <div className="text-sm text-muted-foreground">Total emails</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-6 text-center">
                <MailOpen className="w-8 h-8 mx-auto mb-2 text-green-500" />
                <div className="text-2xl font-bold">{emails.filter(e => e.isRead).length}</div>
                <div className="text-sm text-muted-foreground">Emails lus</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-6 text-center">
                <Clock className="w-8 h-8 mx-auto mb-2 text-orange-500" />
                <div className="text-2xl font-bold">{emails.filter(e => !e.isRead).length}</div>
                <div className="text-sm text-muted-foreground">Non lus</div>
              </CardContent>
            </Card>
          </div>

          {emails.length === 0 ? (
            <Card>
              <CardContent className="p-12 text-center">
                <Mail className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-xl font-semibold mb-2">Aucun email reçu</h3>
                <p className="text-muted-foreground">
                  Les messages de contact apparaîtront ici dès qu'un visiteur utilisera le formulaire.
                </p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

              {/* Liste des emails */}
              <div className="space-y-4">
                <h2 className="text-2xl font-bold mb-4">Messages reçus</h2>
                {emails.map((email) => {
                  const parsedData = parseEmailContent(email.content);
                  return (
                    <Card 
                      key={email.id} 
                      className={`cursor-pointer transition-all hover:shadow-md ${
                        selectedEmail?.id === email.id ? 'ring-2 ring-primary' : ''
                      } ${!email.isRead ? 'border-primary' : ''}`}
                      onClick={() => setSelectedEmail(email)}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-center justify-between mb-2">
                          <div className="flex items-center space-x-2">
                            {email.isRead ? (
                              <MailOpen className="w-5 h-5 text-green-500" />
                            ) : (
                              <Mail className="w-5 h-5 text-primary" />
                            )}
                            <span className="font-semibold">{email.fromName}</span>
                            {!email.isRead && <Badge variant="default">Nouveau</Badge>}
                          </div>
                          <span className="text-sm text-muted-foreground">
                            {formatDate(email.createdAt)}
                          </span>
                        </div>
                        <p className="text-sm text-muted-foreground mb-1">{email.fromEmail}</p>
                        {parsedData.budget && (
                          <p className="text-sm font-medium text-green-600">Budget: {parsedData.budget}</p>
                        )}
                        <p className="text-sm text-muted-foreground line-clamp-2 mt-2">
                          {parsedData.message}
                        </p>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>

              {/* Détail de l'email sélectionné */}
              <div className="lg:sticky lg:top-32">
                {selectedEmail ? (
                  <Card>
                    <CardHeader>
                      <div className="flex items-center justify-between">
                        <h3 className="text-xl font-bold">{selectedEmail.subject}</h3>
                        {!selectedEmail.isRead && (
                          <Button 
                            size="sm"
                            onClick={() => markAsRead(selectedEmail.id)}
                          >
                            Marquer comme lu
                          </Button>
                        )}
                      </div>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      {(() => {
                        const data = parseEmailContent(selectedEmail.content);
                        return (
                          <>
                            <div className="grid grid-cols-1 gap-3">
                              <div className="flex items-center space-x-2">
                                <User className="w-4 h-4 text-primary" />
                                <span className="font-medium">{data.name}</span>
                              </div>
                              <div className="flex items-center space-x-2">
                                <Mail className="w-4 h-4 text-primary" />
                                <a href={`mailto:${data.email}`} className="text-primary hover:underline">
                                  {data.email}
                                </a>
                              </div>
                              {data.phone && data.phone !== 'Non renseigné' && (
                                <div className="flex items-center space-x-2">
                                  <Phone className="w-4 h-4 text-primary" />
                                  <a href={`tel:${data.phone}`} className="text-primary hover:underline">
                                    {data.phone}
                                  </a>
                                </div>
                              )}
                              {data.budget && data.budget !== 'Non spécifié' && (
                                <div className="flex items-center space-x-2">
                                  <Euro className="w-4 h-4 text-green-600" />
                                  <span className="font-medium text-green-600">{data.budget}</span>
                                </div>
                              )}
                              {data.projectType && data.projectType !== 'Non spécifié' && (
                                <div className="flex items-center space-x-2">
                                  <Badge variant="outline">{data.projectType}</Badge>
                                </div>
                              )}
                            </div>

                            <Separator />

                            <div>
                              <div className="flex items-center space-x-2 mb-3">
                                <MessageSquare className="w-4 h-4 text-primary" />
                                <span className="font-medium">Message:</span>
                              </div>
                              <div className="bg-muted p-4 rounded-lg">
                                <p className="whitespace-pre-wrap">{data.message}</p>
                              </div>
                            </div>

                            <div className="text-sm text-muted-foreground">
                              Reçu le {formatDate(selectedEmail.createdAt)}
                            </div>
                          </>
                        );
                      })()}
                    </CardContent>
                  </Card>
                ) : (
                  <Card>
                    <CardContent className="p-12 text-center">
                      <Mail className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
                      <h3 className="text-xl font-semibold mb-2">Sélectionnez un email</h3>
                      <p className="text-muted-foreground">
                        Cliquez sur un message à gauche pour voir les détails complets.
                      </p>
                    </CardContent>
                  </Card>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/blog-article.tsx.content:
import { useEffect } from 'react';
import { Link, useParams } from 'wouter';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Calendar, Clock, User, ArrowLeft, Share, BookOpen } from 'lucide-react';
import Breadcrumbs from '@/components/ui/breadcrumbs';

const scrollToTop = () => {
  window.scrollTo({ top: 0, behavior: 'smooth' });
};

const blogPosts = [
  {
    id: 1,
    title: "Pourquoi travailler avec une agence humaine ?",
    excerpt: "À l'ère de l'automatisation, découvrez pourquoi l'approche humaine fait toute la différence dans la création de votre site web.",
    content: `Dans un monde où les templates automatisés et les constructeurs de sites pullulent, choisir une agence humaine peut sembler contre-intuitif. Pourtant, c'est exactement cette approche personnalisée qui transforme un simple site web en véritable outil de croissance pour votre entreprise.

## L'humain au cœur de la création

Chez Weblify Studio, chaque projet commence par une conversation. Pas un formulaire automatisé, pas un chatbot, mais un vrai échange avec Noah, le fondateur. Cette approche nous permet de comprendre non seulement vos besoins techniques, mais aussi vos ambitions, votre personnalité et votre vision.

## Créativité vs Standardisation

Les outils automatisés vous proposent des templates prémâchés. Nous, nous créons votre identité numérique unique. Chaque couleur, chaque typographie, chaque interaction est pensée pour VOTRE marque, pas pour ressembler aux milliers d'autres sites générés par les mêmes outils.

## Support réactif et personnalisé

Quand vous avez une question à 23h un vendredi, vous n'avez pas envie de parler à un robot. Notre support humain comprend vos urgences, vos contraintes métier et peut prendre des décisions rapides pour débloquer vos situations.

## Évolution continue

Un site web n'est jamais "fini". Avec une agence humaine, vous avez un partenaire qui grandit avec votre entreprise, qui comprend votre évolution et peut anticiper vos besoins futurs.`,
    author: "Noah Delenclos",
    publishDate: "2025-01-15",
    readTime: "5 min",
    category: "Stratégie",
    slug: "pourquoi-agence-humaine",
    featured: true,
    image: "https://images.unsplash.com/photo-1552664730-d307ca884978?w=800&h=400&fit=crop&q=80"
  }
];

export default function BlogArticle() {
  const params = useParams();
  const slug = params.slug;

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, []);

  const post = blogPosts.find(p => p.slug === slug);

  if (!post) {
    return (
      <div className="pt-28 pb-16">
        <div className="container mx-auto px-6 text-center">
          <h1 className="text-4xl font-bold mb-4">Article non trouvé</h1>
          <p className="text-muted-foreground mb-8">Cet article n'existe pas ou a été supprimé.</p>
          <Link href="/blog" onClick={scrollToTop}>
            <Button>Retour au blog</Button>
          </Link>
        </div>
      </div>
    );
  }

  const formatContent = (content: string) => {
    const lines = content.split('\n');
    return lines.map((line, index) => {
      if (line.startsWith('## ')) {
        return <h2 key={index} className="text-2xl font-bold mt-8 mb-4 text-primary">{line.replace('## ', '')}</h2>;
      }
      if (line.startsWith('**') && line.endsWith('**')) {
        return <p key={index} className="font-semibold mb-4">{line.replace(/\*\*/g, '')}</p>;
      }
      if (line.trim() === '') {
        return <div key={index} className="mb-4"></div>;
      }
      return <p key={index} className="mb-4 leading-relaxed">{line}</p>;
    });
  };

  return (
    <div className="pt-28 pb-16">
      <div className="container mx-auto px-6 max-w-4xl">
        {/* Breadcrumbs */}
        <Breadcrumbs className="mb-8" items={[
          { label: 'Blog', href: '/blog' },
          { label: post.title }
        ]} />

        {/* Navigation */}
        <div className="mb-8">
          <Link href="/blog" onClick={scrollToTop}>
            <Button variant="ghost" className="mb-4 hover:bg-primary/10">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Retour au blog
            </Button>
          </Link>
        </div>

        {/* Article Header */}
        <article className="space-y-8">
          <header className="space-y-6">
            <div className="flex items-center space-x-3">
              <Badge variant="outline" className="border-primary text-primary">
                {post.category}
              </Badge>
              {post.featured && (
                <Badge variant="secondary" className="bg-orange-100 text-orange-600">
                  À la une
                </Badge>
              )}
            </div>

            <h1 className="text-4xl md:text-5xl font-bold leading-tight">
              {post.title}
            </h1>

            <p className="text-xl text-muted-foreground leading-relaxed">
              {post.excerpt}
            </p>

            <div className="flex items-center justify-between py-4 border-t border-b">
              <div className="flex items-center space-x-6 text-sm text-muted-foreground">
                <div className="flex items-center space-x-2">
                  <User className="w-4 h-4" />
                  <span className="font-medium">{post.author}</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Calendar className="w-4 h-4" />
                  <span>{new Date(post.publishDate).toLocaleDateString('fr-FR', { 
                    year: 'numeric', 
                    month: 'long', 
                    day: 'numeric' 
                  })}</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Clock className="w-4 h-4" />
                  <span>{post.readTime}</span>
                </div>
              </div>
              <Button variant="outline" size="sm">
                <Share className="w-4 h-4 mr-2" />
                Partager
              </Button>
            </div>
          </header>

          {/* Featured Image */}
          <div className="relative h-64 md:h-96 overflow-hidden rounded-2xl">
            <img 
              src={post.image} 
              alt={post.title}
              className="w-full h-full object-cover"
            />
          </div>

          {/* Article Content */}
          <div className="prose prose-lg max-w-none">
            <div className="space-y-4 text-lg leading-relaxed">
              {formatContent(post.content)}
            </div>
          </div>

          {/* Article Footer */}
          <footer className="pt-8 border-t space-y-8">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center">
                  <User className="w-6 h-6 text-primary" />
                </div>
                <div>
                  <p className="font-semibold">{post.author}</p>
                  <p className="text-sm text-muted-foreground">Fondateur de Weblify Studio</p>
                </div>
              </div>
              <Button variant="outline">
                <Share className="w-4 h-4 mr-2" />
                Partager cet article
              </Button>
            </div>

            <Card className="bg-gradient-to-r from-primary/5 to-blue-600/5 border-primary/20">
              <CardContent className="p-6 text-center">
                <BookOpen className="w-8 h-8 text-primary mx-auto mb-4" />
                <h3 className="text-xl font-semibold mb-2">Vous avez aimé cet article ?</h3>
                <p className="text-muted-foreground mb-4">
                  Découvrez nos autres conseils et retours d'expérience sur le blog
                </p>
                <Link href="/blog" onClick={scrollToTop}>
                  <Button>
                    Voir tous les articles
                  </Button>
                </Link>
              </CardContent>
            </Card>
          </footer>
        </article>
      </div>
    </div>
  );
}

