    // Version texte pour compatibilit√©
    const emailText = `
NOUVEAU CONTACT - Weblify Studio
================================

Nom: ${contactData.name}
Email: ${contactData.email}
T√©l√©phone: ${contactData.phone || 'Non renseign√©'}
