import { Request, Response } from 'express';
import { z } from 'zod';
import { storage } from './storage';
import { sendNewsletterConfirmationEmail, sendNewsletterWelcomeEmail } from './email-templates';
import { mailchimpService } from './mailchimp';

// Validation des donn√©es newsletter
const newsletterSchema = z.object({
  email: z.string().email('Email invalide').min(1, 'Email requis'),
  firstName: z.string().min(2, 'Pr√©nom requis (min 2 caract√®res)').max(50).optional(),
  lastName: z.string().min(2, 'Nom requis (min 2 caract√®res)').max(50).optional(),
  interests: z.array(z.string()).optional().default([]),
  source: z.string().optional().default('website'),
});

const unsubscribeSchema = z.object({
  email: z.string().email('Email invalide'),
  token: z.string().min(1, 'Token requis'),
});

// G√©n√©ration de token de d√©sabonnement s√©curis√©
function generateUnsubscribeToken(email: string): string {
  const timestamp = Date.now();
  const secret = process.env.NEWSLETTER_SECRET || 'default-secret-key';
  const data = `${email}:${timestamp}:${secret}`;

  // Simple hash (en production, utiliser crypto.createHash)
  return Buffer.from(data).toString('base64').replace(/[^a-zA-Z0-9]/g, '').substring(0, 32);
}

// Validation du token de d√©sabonnement
function validateUnsubscribeToken(email: string, token: string): boolean {
  // En production, impl√©menter une validation plus robuste avec crypto
  return token.length === 32;
}

// Inscription √† la newsletter
export async function subscribeToNewsletter(req: Request, res: Response) {
  try {
    const validatedData = newsletterSchema.parse(req.body);

    // V√©rifier si l'email existe d√©j√†
    const existingSubscriber = await storage.getNewsletterSubscriber(validatedData.email);

    if (existingSubscriber) {
      if (existingSubscriber.isActive) {
        return res.status(400).json({
          message: 'Cet email est d√©j√† abonn√© √† notre newsletter'
        });
      } else {
        // R√©activer l'abonnement
        await storage.updateNewsletterSubscriber(validatedData.email, {
          isActive: true,
          firstName: validatedData.firstName || 'Pr√©nom',
          lastName: validatedData.lastName || 'Nom',
          interests: validatedData.interests,
          subscribedAt: new Date(),
        });

        await sendNewsletterWelcomeEmail(validatedData.email, validatedData.firstName || 'Abonn√©');

        return res.json({
          success: true,
          message: 'Abonnement r√©activ√© avec succ√®s !'
        });
      }
    }

    // Cr√©er un nouvel abonnement
    const unsubscribeToken = generateUnsubscribeToken(validatedData.email);

    const subscriber = await storage.createNewsletterSubscriber({
      email: validatedData.email,
      firstName: validatedData.firstName || 'Pr√©nom',
      lastName: validatedData.lastName || 'Nom',
      interests: validatedData.interests,
      source: validatedData.source,
      unsubscribeToken,
      isActive: true,
    });

    // Ajouter √† Mailchimp (si configur√©)
    const mailchimpListId = process.env.MAILCHIMP_LIST_ID;
    if (mailchimpListId) {
      const mailchimpResult = await mailchimpService.addSubscriber({
        email: validatedData.email,
        firstName: validatedData.firstName || 'Pr√©nom',
        lastName: validatedData.lastName || 'Nom',
        interests: validatedData.interests,
        source: validatedData.source
      }, mailchimpListId);

      if (mailchimpResult.success) {
        console.log(`‚úÖ Abonn√© ajout√© √† Mailchimp : ${validatedData.email}`);
      } else {
        console.warn(`‚ö†Ô∏è Erreur Mailchimp : ${mailchimpResult.error}`);
      }
    }

    // Envoyer email de confirmation
    await sendNewsletterConfirmationEmail(
      validatedData.email, 
      validatedData.firstName || 'Abonn√©',
      unsubscribeToken
    );

    res.json({
      success: true,
      message: 'Inscription r√©ussie ! V√©rifiez votre email pour confirmer votre abonnement.'
    });

  } catch (error) {
    console.error('Erreur inscription newsletter:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({
        message: 'Donn√©es invalides',
        errors: error.errors.map(e => ({
          field: e.path.join('.'),
          message: e.message
        }))
      });
    }

    res.status(500).json({
      message: 'Erreur lors de l\'inscription √† la newsletter'
    });
  }
}

// D√©sabonnement de la newsletter
export async function unsubscribeFromNewsletter(req: Request, res: Response) {
  try {
    const { email, token } = unsubscribeSchema.parse(req.query);

    // Valider le token
    if (!validateUnsubscribeToken(email, token)) {
      return res.status(400).json({
        message: 'Token de d√©sabonnement invalide'
      });
    }

    // V√©rifier si l'abonnement existe
    const subscriber = await storage.getNewsletterSubscriber(email);
    if (!subscriber) {
      return res.status(404).json({
        message: 'Abonnement introuvable'
      });
    }

    // D√©sactiver l'abonnement
    await storage.updateNewsletterSubscriber(email, {
      isActive: false,
      unsubscribedAt: new Date(),
    });

    res.json({
      success: true,
      message: 'D√©sabonnement r√©ussi. Vous ne recevrez plus nos newsletters.'
    });

  } catch (error) {
    console.error('Erreur d√©sabonnement newsletter:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({
        message: 'Param√®tres invalides',
        errors: error.errors
      });
    }

    res.status(500).json({
      message: 'Erreur lors du d√©sabonnement'
    });
  }
}

// Obtenir les statistiques de la newsletter (admin)
export async function getNewsletterStats(req: Request, res: Response) {
  try {
    const stats = await storage.getNewsletterStats();

    res.json({
      success: true,
      data: stats
    });

  } catch (error) {
    console.error('Erreur stats newsletter:', error);
    res.status(500).json({
      message: 'Erreur lors de la r√©cup√©ration des statistiques'
    });
  }
}

// Mise √† jour des pr√©f√©rences d'un abonn√©
export async function updateNewsletterPreferences(req: Request, res: Response) {
  try {
    const { email } = req.params;
    const { interests, firstName, lastName } = req.body;

    const subscriber = await storage.getNewsletterSubscriber(email);
    if (!subscriber || !subscriber.isActive) {
      return res.status(404).json({
        message: 'Abonnement introuvable ou inactif'
      });
    }

    await storage.updateNewsletterSubscriber(email, {
      interests: interests || subscriber.interests,
      firstName: firstName || subscriber.firstName,
      lastName: lastName || subscriber.lastName,
    });

    res.json({
      success: true,
      message: 'Pr√©f√©rences mises √† jour avec succ√®s'
    });

  } catch (error) {
    console.error('Erreur mise √† jour pr√©f√©rences:', error);
    res.status(500).json({
      message: 'Erreur lors de la mise √† jour des pr√©f√©rences'
    });
  }
}

