  'application/vnd.ms-excel': { icon: 'üìä', name: 'Excel' },
  'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': { icon: 'üìä', name: 'Excel' },
  'image/jpeg': { icon: 'üñºÔ∏è', name: 'Image' },
