const categoryLabels = {
  general: 'G√©n√©ral',
  technique: 'Technique',
  prix: 'Tarifs',
  delais: 'D√©lais',
  maintenance: 'Maintenance'
};

const categoryIcons = {
  general: MessageSquare,
  technique: Settings,
  prix: Euro,
  delais: Clock,
  maintenance: Settings
};

export default function InteractiveFAQ() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  const filteredFAQs = useMemo(() => {
    let filtered = faqData;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(
        faq =>
          faq.question.toLowerCase().includes(searchTerm.toLowerCase()) ||
          faq.answer.toLowerCase().includes(searchTerm.toLowerCase()) ||
          faq.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(faq => faq.category === selectedCategory);
    }

    // Sort popular items first
    return filtered.sort((a, b) => {
      if (a.popular && !b.popular) return -1;
      if (!a.popular && b.popular) return 1;
      return 0;
    });
  }, [searchTerm, selectedCategory]);

  const categories = Object.keys(categoryLabels) as Array<keyof typeof categoryLabels>;

  return (
    <div className="space-y-8">
      {/* Search and Filters */}
      <div className="space-y-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
          <Input
            type="text"
            placeholder="Rechercher dans les questions fr√©quentes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
          />
        </div>

        {/* Category Filters */}
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => setSelectedCategory('all')}
            className={`px-4 py-2 rounded-full text-sm font-medium transition-all ${
              selectedCategory === 'all'
                ? 'bg-primary text-white'
                : 'bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
            }`}
          >
            Toutes les questions
          </button>
          {categories.map((category) => {
            const Icon = categoryIcons[category];
            const count = faqData.filter(faq => faq.category === category).length;
            return (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-4 py-2 rounded-full text-sm font-medium transition-all flex items-center space-x-2 ${
                  selectedCategory === category
                    ? 'bg-primary text-white'
                    : 'bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
                }`}
              >
                <Icon className="w-4 h-4" />
                <span>{categoryLabels[category]}</span>
                <span className="bg-white/20 px-2 py-0.5 rounded-full text-xs">
                  {count}
                </span>
              </button>
            );
          })}
        </div>
      </div>



      {/* Search Results Count */}
      {(searchTerm || selectedCategory !== 'all') && (
        <div className="text-sm text-gray-600 dark:text-gray-300">
          {filteredFAQs.length} question{filteredFAQs.length > 1 ? 's' : ''} trouv√©e{filteredFAQs.length > 1 ? 's' : ''}
          {searchTerm && ` pour "${searchTerm}"`}
          {selectedCategory !== 'all' && ` dans la cat√©gorie "${categoryLabels[selectedCategory as keyof typeof categoryLabels]}"`}
        </div>
      )}

      {/* FAQ Accordion */}
      <Accordion type="single" collapsible className="space-y-4">
        {filteredFAQs.map((faq) => (
          <AccordionItem
            key={faq.id}
            value={faq.id}
            id={`faq-${faq.id}`}
            className="bg-white dark:bg-gray-800 rounded-lg border shadow-sm hover:shadow-md transition-shadow"
          >
            <AccordionTrigger className="px-6 py-4 text-left hover:no-underline">
              <div className="flex items-start justify-between w-full">
                <div className="flex-1 pr-4">
                  <h4 className="font-medium text-base mb-2">{faq.question}</h4>
                  <div className="flex items-center space-x-2">
                    <Badge variant="secondary" className="text-xs">
                      {categoryLabels[faq.category]}
                    </Badge>
                    {faq.popular && (
                      <Badge className="text-xs bg-primary">
                        Populaire
                      </Badge>
                    )}
                  </div>
                </div>
              </div>
            </AccordionTrigger>
            <AccordionContent className="px-6 pb-6">
              <div className="text-gray-700 dark:text-gray-300 leading-relaxed">
                {faq.answer}
              </div>
              <div className="flex flex-wrap gap-1 mt-4">
                {faq.tags.map((tag, index) => (
                  <Badge key={index} variant="outline" className="text-xs">
                    #{tag}
                  </Badge>
                ))}
              </div>
            </AccordionContent>
          </AccordionItem>
        ))}
      </Accordion>

      {/* No Results */}
      {filteredFAQs.length === 0 && (
        <div className="text-center py-12">
          <MessageSquare className="w-12 h-12 text-gray-300 dark:text-gray-600 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-600 dark:text-gray-300 mb-2">
            Aucune question trouv√©e
          </h3>
          <p className="text-gray-500 dark:text-gray-400 mb-4">
            Essayez d'autres mots-cl√©s ou consultez toutes les questions.
          </p>
          <button
            onClick={() => {
              setSearchTerm('');
              setSelectedCategory('all');
            }}
            className="text-primary hover:underline font-medium"
          >
            R√©initialiser les filtres
          </button>
        </div>
      )}
    </div>
  );
}

